import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useMoralis } from "react-moralis";
import { Card, Image, Tooltip, Modal, Input, Alert, Spin, Button } from "antd";
import { FileSearchOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import NFTMinter from './NFTMinter'
import {
  BrowserRouter as Router,
  NavLink,
} from "react-router-dom";

const { Meta } = Card;
const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

const Collection = () => {
  let params = useParams();
  const [nfts,setNFTs] = useState();
  const { Moralis, isInitialized } = useMoralis();
  const collectionId = params.collectionId
  useEffect(() => {
    const getData = async () => {

      const options = {
        chain: "0x13881",
        token_address: collectionId,
      };
      const nftData = await Moralis.Web3API.account.getNFTsForContract(options);
      const jsonNFTs = nftData.result.map((nft)=>{return {token_id:nft.token_id, metadata:JSON.parse(nft.metadata)}})
      setNFTs(jsonNFTs);
    }
    if (isInitialized){
      getData()
    }

  },[isInitialized]);

  return (
    <>
      <NFTMinter collectionId={collectionId}/>
      <div style={styles.NFTs}>
        {nfts &&
          nfts.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>console.log("")}
                  />
                </Tooltip>,
                <Tooltip title="List NFT for sale">
                  <ShoppingCartOutlined onClick={() => console.log("")} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <NavLink to={`/collections/${collectionId}/${nft.token_id}`}>
                  <Image
                    preview={false}
                    src={"https://gateway.ipfs.io/ipfs/"+nft.metadata.image.split("//")[1] }
                    fallback="data:image/png;base64,"
                    alt=""
                    style={{ height: "240px" }}
                  />
                </NavLink>
              }
              key={index}
            >
              <Meta title={nft.metadata.name} description={nft.metadata.description} />
            </Card>
          ))}
      </div>
    </>
  );
}


export default Collection;
